package logfactory;/** * @author 		Ermerson Rafael - 816122707 * @category	Aplicações e Serviços * @since		21/02/2018 * @version		v1.0.0.4 */public abstract class EscrevedorLog {	private FerramentaLog log;	protected abstract FerramentaLog criarFerramentaLog();	public void escreverLog(String mensagem) {		if (log == null)			log = criarFerramentaLog();		log.registrarLog(mensagem);	}}