package nomesfactory;/** * @author 		Ermerson Rafael - 816122707 * @category	Aplicações e Serviços * @since		21/02/2018 * @version		v1.0.0.4 */public class AplicacaoNomesNormal extends AplicacaoNomes {	public Nome construirNome(String nome) {		String sobrenome = "";		int pos = nome.indexOf(" ");		if (pos != -1) {			sobrenome = nome.substring(pos + 1, nome.length()).trim();			nome = nome.substring(0, pos).trim();		}		return new Nome(nome, sobrenome);	}}