package nomesfactory;import javax.swing.JOptionPane;/** * @author 		Ermerson Rafael - 816122707 * @category	Aplicações e Serviços * @since		21/02/2018 * @version		v1.0.0.4 */public class Main {	public static void main(String[] args) {		String nomeCompleto = JOptionPane.showInputDialog(null, "Digite o nome completo:");		AplicacaoNomes aplicacaoNormal = new AplicacaoNomesNormal();		AplicacaoNomes aplicacaoInvertido = new AplicacaoNomesInvertido();		for (int i = 0; i < nomeCompleto.length(); i++) {			if (nomeCompleto.indexOf(",") != -1)				aplicacaoInvertido.adicionarNome(nomeCompleto);			else				aplicacaoNormal.adicionarNome(nomeCompleto);		}		aplicacaoNormal.imprimirNomes();		aplicacaoInvertido.imprimirNomes();	}}