package nomesfactory;/** * @author 		Ermerson Rafael - 816122707 * @category	Aplicações e Serviços * @since		21/02/2018 * @version		v1.0.0.4 */public class Nome {	private String nome;	private String sobrenome;	public Nome(String nome, String sobrenome) {		this.nome = nome;		this.sobrenome = sobrenome;	}	public String toString() {		return nome + " " + sobrenome;	}}